export interface ConversationActions {
    tags: {
        operator: 'append' | 'replace' | 'remove';
        value: string[];
    };
    name: {
        operator: 'replace' | 'remove';
        value: string;
    };
    status: {
        operator: 'replace' | 'remove';
        value: 'open' | 'done';
    };
    subStatus: {
        operator: 'replace' | 'remove';
        value: 'string';
    };
    snooze: {
        operator: 'replace';
        value: string;
    };
    queue: {
        operator: 'replace';
        value: {
            id: string | null;
            external: 'amazon-connect' | null;
        };
    };
    assignedToUsers: {
        operator: 'append' | 'replace' | 'remove';
        value: any[];
    };
    assignedToTeams: {
        operator: 'append' | 'replace' | 'remove';
        value: any[];
    };
    custom: {
        [key: string]: {
            operator: 'replace';
            value: string | boolean | number | null;
        };
    };
}
