export interface KlassOptions {
    /** A Material-UI icon name for displaying alongisde the Klass */
    icon: string;
    /** A valid css color that will be used to display the icon in a specific color within Kustomer */
    color: string;
    /** The name of the Klass */
    metadata: {
        properties: {
            /** A property name key, with a suffix to explain what type of field this is (eg, str, txt, num, bool, url) */
            [propertyName: string]: {
                /** What the field will be represented as in the Kustomer UI */
                [displayName: string]: string;
            };
        };
    };
}
/**
 * The customizable data type within Kustomer. This allows you to create new kobjects within the Kustomer platform.
 */
export interface Klass extends KlassOptions {
    /** The name of the Klass */
    name: string;
}
